import React, { useState, useEffect } from 'react';
import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import { userDelete, userEdit, userIndex } from '../Redux/Action'
import { Checkbox } from 'antd'

function Rtable (props) {
  
  const [data, setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]});
  const [userData, setUserData]=useState([])
  const [index, setIndex]=useState('')
  const [deleteall, setDeleteAll]=useState([])
  const [check, setCheck]=useState([])

  useEffect(() => setUserData(props.tableData),[props.tableData])
  useEffect(() => setDeleteAll(userData),[])
  console.log("useEffecttableData------>",props.tableData)

  const EditRow = (dat,i,r) => {
    console.log("iiiiiiiiii----->",i)
    console.log("edat------>",dat)
    setIndex(r)
    props.userEdit(i)
    props.userIndex(r)
    props.navigate("/")
    console.log("rrrrrrr------->",r)
  }
  
  const DeleteRow = (dat,i,r) => {
    console.log("ddat------->",dat)
    const Data = props.tableData;
    console.log("props.tableData------->",props.tableData)
    Data.splice(r,1)
    setUserData([...Data])
    console.log("spliceData------>",userData)
    props.userDelete(Data)
  }

  const GoBack = () =>{
    props.navigate("/")
  }

  const onSearch = (e) => {
    console.log("searchvalue------>",e.target.value)
    if(e.target.value === ""){
      setUserData(props.tableData)
    }
    else{
      const searchData = userData.filter(Item => Item.fname.includes(e.target.value) || Item.mname.includes(e.target.value) || Item.lname.includes(e.target.value))
      console.log("searchuserData",userData)
      setUserData(searchData)
      console.log("searchData",searchData)
    }
  }

  const hendleCheck = (e,check,r) => {
    const deleteRow = data
  if (deleteRow === "check"){
    console.log("hiiiiiii")
    if (e.target.checked === true){
      console.log("hiii")
    deleteRow.push(e.target.name)
    }
    else{
      const index = data.check.indexOf(e.target.name)
      deleteRow.splice(index, 1)
    }
    setData({
      ...data,setUserData
    })
}

const deleteAllRow = (e,check,r) => {
  console.log("eeeeeeeeee",e)
    const deleteRow = props.tableData;
    console.log("props.tableData------->",props.tableData)
    deleteRow.splice(r,1)
    setUserData([...deleteRow])
    console.log("spliceData------>",userData)
    props.userDelete(deleteRow)
 }
}

