const column = [
    {
      title: 'CHECKED',
      dataIndex: 'check',
      key: 'check',
      render:(e,check,r) => (
        <>
        <Checkbox onChange={(e) => hendleCheck(e,check,r)} />
        </>
      )
    },
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
      render:(dat,i,r) => (
        <>
          <EditOutlined style={{color:'blue'}} onClick={() => EditRow(dat,i,r)} />
          <DeleteOutlined style={{color:'red', marginLeft:3}} onClick={() => DeleteRow(dat,i,r)} />
        </>
      )
    }
  ];

  return(
    <>
    <label style={{color:'blue'}}><u><b>Search TableData :</b></u></label>
    <input style={{ margin:10 , marginleft:20 }} type="text" placeholder='Search...' onChange={(e) => onSearch (e)} />
    <button onClick={(e,r) => deleteAllRow(e,r)}>Delete All</button><br />
    <Table dataSource={userData} columns={column} />
    <button onClick={GoBack}>Go Back</button>
    </>
  )
 };

const mapStateToProps = (state) =>{
  return{
    tableData:state.data,
    uEdit:state.UserEdit,
    uIndex:state.UserIndex,
  }
}
const mapDispatchToProps = (dispatch) =>{
  return{
   userDelete:(data) => dispatch(userDelete(data)),
   userEdit: (data) => dispatch(userEdit(data)),
   userIndex: (data) => dispatch(userIndex(data)),
  }
}
 
export default connect(mapStateToProps,mapDispatchToProps) (Rtable)
