import { Checkbox } from 'antd'
import React, { useState } from 'react'
import { connect } from 'react-redux'
import Rtable from './Component/Table'
import { userSubmit , userDelete } from './Redux/Action'

const App = (props) => {

  const [data , setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]})
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState("")

const hendleOnChange = (e, language) => {
  console.log("eeeeeee------>",e.target.value)
  const languageData = data.language
  const gameData = data.game
  // console.log("languageData", languageData)
  // console.log("gameData", gameData)
  // console.log("language", data.language)
  // console.log("game", data.game)
if (language === "language"){
  if (e.target.checked === true){
  languageData.push(e.target.name)
  }
  else{
    const index = data.language.indexOf(e.target.name)
    languageData.splice(index, 1)
  }
  setData({
    ...data,language:languageData
  })
}
if(language === "game"){
  if (e.target.checked === true){
  gameData.push(e.target.name)
  }
  else{
    const index = data.game.indexOf(e.target.name)
    gameData.splice(index, 1)
  }
  setData({
    ...data,game:gameData
  })
}
  else{
  setData({...data, [e.target.name]:e.target.value})
  }
}

const hendleOnSubmit = () => {
  const Data = props.tableData
  console.log("Data---------->",Data)
  if(index === ""){
    Data.push(data)
  }
  else{
    Data[index] = data
    setIndex("")
  }
  setUserData([...Data])
  setData({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]})
}


const EditRow = (dat,i,r) => {
  console.log("iiiiiiiiii----->",i)
  setData({
    fname:i.fname,
    mname:i.mname,
    lname:i.lname,
    gender:i.gender,
    country:i.country,
    city:i.city,
    language:i.language,
    game:i.game
  })
  setIndex(r)
  console.log("rrrrrrr------->",r)
}

const DeleteRow = (dat,i,r) => {
  const Data = userData
  console.log("userData------->",userData)
  Data.splice(r,1)
  console.log("spliceData------>",Data)
  setUserData([...Data])
}

  return (
    <>
    <div style={{ margin:10 , marginleft:20 }}>
      <label><b>First Name : </b></label>
      <input type="text" name='fname' placeholder='Enter Your First Name' value={data.fname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b>Middle Name : </b></label>
      <input type="text" name='mname' placeholder='Enter Your middle Name' value={data.mname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b>Last Name : </b></label>
      <input type="text" name='lname' placeholder='Enter Your last Name' value={data.lname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b> Age : </b></label>
      <input type="tel" name='age' placeholder='Enter Your Age' /><br />
      <lable><b> Select Your Gender : </b></lable><br />
      <label> Male </label>
      <input type="radio" name='gender' id='male' value='male' checked={data.gender === 'male'} onChange={(e) => hendleOnChange(e)} />
      <label> Female </label>
      <input type="radio" name='gender' id='female' value='female' checked={data.gender === 'female'} onChange={(e) => hendleOnChange(e)} />
      <label> Other </label>
      <input type="radio" name='gender' id='other' value='other' checked={data.gender === 'other'} onChange={(e) => hendleOnChange(e)} /><br />
      <lable><b> Select Your Country : </b></lable>
      <select
      type="selected" 
      name="country" 
      onChange={(e) => hendleOnChange(e)}>
        <option value="" selected={data.country === ""}>Select Your Country</option>
        <option value="India" selected={data.country === "India"}>India</option>
        <option value="Pakistan" selected={data.country === "Pakistan"}>Pakistan</option>
        <option value="Rasia" selected={data.country === "Rasia"}>Rasia</option>
        <option value="New Zelend" selected={data.country === "New Zelend"}>New Zelend</option>
      </select><br />
      <label><b> Select Your City : </b></label>
      <select 
      type="selected" 
      name="city"
      onChange={(e) => hendleOnChange(e)}>
        <option value="" selected={data.city === ""}>Select Your City</option>
        <option value="Rajkot" selected={data.city === "Rajkot"}>Rajkot</option>
        <option value="Surat" selected={data.city === "Surat"}>Surat</option>
        <option value="Bharuch" selected={data.city === "Bharuch"}>Bharuch</option>
        <option value="talala" selected={data.city === "Talala"}>Talala</option>
      </select><br />
      <label><b> Select Your Language : </b></label><br />
      <Checkbox 
      type="checkbox"
      name='English'
      value='English'
      onChange={(e) => hendleOnChange(e,"language")} 
      checked={data.language.includes("English")}>English
      </Checkbox>
      <Checkbox 
      type="checkbox"
      name='Hindi'
      value='Hindi'
      onChange={(e) => hendleOnChange(e,"language")} 
      checked={data.language.includes("Hindi")}>Hindi
      </Checkbox>
      <Checkbox 
      type="checkbox"
      name='Gujrati'
      value='Gujrati'
      onChange={(e) => hendleOnChange(e,"language")} 
      checked={data.language.includes("Gujrati")}>Gujrati
      </Checkbox><br />
      <label><b> Select Your Favourite Game : </b></label><br />
      <Checkbox 
      type="checkbox"
      name='BGMI'
      value='BGMI'
      onChange={(e) => hendleOnChange(e,"game")} 
      checked={data.game.includes("BGMI")}>BGMI
      </Checkbox>
      <Checkbox 
      type="checkbox"
      name='Free Fire'
      value='Free Fire'
