import React from 'react'
import { useState } from 'react';
import { Modal,Button } from 'antd';

const modal = (props) => {
    const [modalOpen, setModalOpen] = useState(false);

    const modalChange = (e) => {
        setModalOpen({...modalOpen, [e.target.name]:e.target.value})
        console.log("e.target.name",e.target.name)
        console.log("e.target.value",e.target.value)
        console.log("modalOpen======>",modalOpen)
      }
      
      const showModal = () => {
        setModalOpen(true);
      };
      
      const handleOk = () => {
        console.log("modalOpen----------->",modalOpen)
        setModalOpen({text:''})
        setModalOpen(false)
        
      };
      
      const handleCancel = () => {
        setModalOpen(false)
      };

  return (
    <div>
      <Button type='primary' style={{margin:5,marginLeft:6 }} onClick={showModal}>Open Modal</Button>
      <Modal title="Basic Modal" open={props.modalOpen} onOk={handleOk} onCancel={handleCancel}>
      <input autoComplete='off' type="text" placeholder='Text Here...' name='text' value={modalOpen.text} onChange={(e) => modalChange(e)} />
      </Modal>
    </div>
  )
}
export default modal;
