import React, { useState } from 'react'
import { connect } from 'react-redux'
import Rtable from './Component/Table'

const App = () => {

  const [data , setData]=useState({fname:'',mname:'',lname:''})
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState("")

const hendleOnChange = (e) => {
  console.log("eeeeeee------>",e.target.value)
  setData({...data, [e.target.name]:e.target.value})
}

const hendleOnSubmit = () => {
  const Data = userData
  console.log("Data---------->",Data)
  if(index === ""){
    Data.push(data)
  }
  else{
    Data[index] = data
    setIndex("")
  }
  setUserData([...Data])
  setData({fname:'',mname:'',lname:''})
}


const EditRow = (dat,i,r) => {
  console.log("iiiiiiiiii----->",i)
  setData({
    fname:i.fname,
    mname:i.mname,
    lname:i.lname
  })
  setIndex(r)
  console.log("rrrrrrr------->",r)
}

const DeleteRow = (dat,i,r) => {
  const Data = userData
  console.log("userData------->",userData)
  Data.splice(r,1)
  console.log("spliceData------>",Data)
  setUserData([...Data])
}

  return (
    <>
    <div style={{ margin:10 , marginleft:20 }}>
      <label><b>FIRST NAME : </b></label>
      <input type="text" name='fname' placeholder='Enter Your First Name' value={data.fname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b>MIDDLE NAME : </b></label>
      <input type="text" name='mname' placeholder='Enter Your middle Name' value={data.mname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b>LAST NAME : </b></label>
      <input type="text" name='lname' placeholder='Enter Your last Name' value={data.lname} onChange={(e) => hendleOnChange(e)} /><br />
      <button style={{margin:5,marginLeft:90 }} onClick={() => hendleOnSubmit()} >Button</button>
      <Rtable userData={userData} DeleteRow={DeleteRow} EditRow={EditRow}></Rtable>
    </div>
    </>
  )
}

const mapStateToProps=(state) => {
  return {
    userData: state.data
  }
}

export default connect(mapStateToProps,null)(App);
