import { Checkbox } from 'antd'
import React, { useState } from 'react'
import { connect } from 'react-redux'
import Rtable from './Component/Table'
import { userSubmit , userDelete } from './Redux/Action'

const App = (props) => {

  const [data , setData]=useState({
    fname:'',
    mname:'',
    lname:'',
    age:'',
    gender:'',
    country:'',city:'',
    language:[],
    game:[]
  });
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState("")
  const [errors, setError]=useState("")

const hendleOnChange = (e, language) => {
  console.log("e.terget.value------>",e.target.value)
  const languageData = JSON.parse(JSON.stringify(data.language))
  // const languageData = data.language
  const gameData = JSON.parse(JSON.stringify (data.game))
  // console.log("languageData", languageData)
  // console.log("gameData", gameData)
  // console.log("language", data.language)
  // console.log("game", data.game)
if (language === "language"){
  console.log("hiiiiiiiiii----->")
  if (e.target.checked){
    console.log("hiii")
    // console.log("gggg",languageData)
  languageData.push(e.target.name)
  console.log("hhh",languageData)
  }
  else{
    const index = data.language.indexOf(e.target.name)
    languageData.splice(index, 1)
  }
  console.log("eee",languageData)
  setData({
    ...data,[language]:languageData
  })
}
else if(language === "game"){  
  if (e.target.checked){
  gameData.push(e.target.name)
  }
  else{
    const index = data.game.indexOf(e.target.name)
    gameData.splice(index, 1)
  }
  setData({
    ...data,game:gameData
  })
}  else{
  setData({...data, [e.target.name]:e.target.value})
  }
}

const formvalidation = (name , value) =>{
  console.log("name------>",name)
  console.log("value------>",value)
  switch(name){
    case ("fname"):
    if(!value){
      return "firstname is required"
    }else{
      return ""
    }
  }
}

const hendleOnSubmit = () => {
  const errors ={}
  const object = data
  Object.keys(object).forEach(key =>{
    const error = formvalidation(key, object[key])
    if(error && error.length > 0){
      errors[key] = error
    }
  })
  if(Object.keys(errors).length > 0){
      setError(errors)
      return 
  }else{
    setError({})
  }
  const Data = props.tableData
  console.log("Data---------->",Data)
  if(index === ""){
    Data.push(data)
  }
  else{
    Data[index] = data
    setIndex("")
  }
  setUserData([...Data])
  setData({fname:'',mname:'',lname:'',age:'',gender:'',country:'',city:'',language:[],game:[]})
}

const EditRow = (dat,i,r) => {
  console.log("iiiiiiiiii----->",i)
  setData({
    fname:i.fname,
    mname:i.mname,
    lname:i.lname,
    age:i.age,
    gender:i.gender,
    country:i.country,
    city:i.city,
    language:i.language,
    game:i.game
  })
  setIndex(r)
  console.log("rrrrrrr------->",r)
}

const DeleteRow = (dat,i,r) => {
  const Data = userData
  console.log("userData------->",userData)
  Data.splice(r,1)
  console.log("spliceData------>",Data)
  setUserData([...Data])
}

  return (
    <>
    <h2>Crud opration</h2>
    <div style={{ margin:10 , marginleft:20 }}>
      <label><b>First Name : </b></label>
      <input type="text" name='fname' placeholder='Enter Your First Name' value={data.fname} onChange={(e) => hendleOnChange(e)} /><br />
