import React, { useState } from 'react'
import { Checkbox } from 'antd'
import { useEffect } from 'react'
import { connect } from 'react-redux'
import { userSubmit , userDelete , userEdit , userIndex } from './Redux/Action'

const App = (props) => {
  const [data , setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]});
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState("")
  const [errors, setError]=useState("")

   useEffect(() => setIndex(props.uIndex),[props.uIndex])
   useEffect(() => setData(props.uEdit),[props.uEdit])
   useEffect(() => setUserData(props.tableData),[props.tableData])
   console.log("setdata------------------>",props.ueEdit)

const hendleOnChange = (e, language) => {
  console.log("e.terget.value------>",e.target.value)
  const languageData = JSON.parse(JSON.stringify(data.language))
  const gameData = JSON.parse(JSON.stringify (data.game))
  // const languageData = data.language
  // console.log("languageData", languageData)
  // console.log("gameData", gameData)
  // console.log("language", data.language)
  // console.log("game", data.game)
if (language === "language"){
  console.log("hiiiiiiiiii----->")
  if (e.target.checked){
    console.log("hiii")
    // console.log("gggg",languageData)
  languageData.push(e.target.name)
  console.log("hhh",languageData)
  }
  else{
    const index = data.language.indexOf(e.target.name)
    languageData.splice(index, 1)
  }
  console.log("eee",languageData)
  setData({
    ...data,language:languageData
  })
}
else if(language === "game"){  
  if (e.target.checked){
  gameData.push(e.target.name)
  }
  else{
    const index = data.game.indexOf(e.target.name)
    gameData.splice(index, 1)
  }
  setData({
    ...data,game:gameData
  })
}  else{
  setData({...data, [e.target.name]:e.target.value})
  }
}

const formvalidation = (name , value) =>{
  console.log("name------>",name)
  console.log("value------>",value)
  switch(name){
    case ("fname"):
    if(!value){
      return "*FirstName is required"
    }else{
      return ""
    }
    case ("mname"):
    if(!value){
      return "*MiddleName is required"
    }else{
      return ""
    }
    case ("lname"):
    if(!value){
      return "*LastName is required"
    }else{
      return ""
    }
    case ("gender"):
    if(!value){
      return "*Gender is required"
    }else{
      return ""
    }
    case ("country"):
    if(!value){
      return "*Country is required"
    }else{
      return ""
    }
    case ("city"):
    if(!value){
      return "*City is required"
    }else{
      return ""
    }
  }
}

const hendleOnSubmit = () => {
  let errors ={}
  console.log("errors--->",errors)
  let object = data
  Object.keys(object).forEach(key =>{
    const error = formvalidation(key, object[key])
    if(error && error.length > 0){
      errors[key] = error
    }
  })
  if(Object.keys(errors).length > 0){
      setError(errors)
      return 
  }else{
    setError({})
  }
  const Data = props.tableData
  console.log("Data---------->",Data)
  console.log("tableData-->",props.tableData)
  if(index === ""){
    Data.push(data)
  }
  else{
    Data[index] = data
    setIndex("")
  }
  setUserData([...Data])

   props.userSubmit(Data)
  setData({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]})
}

