import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';

const Rtable = (props) =>{
  const [data , setData]=useState({
    fname:'',
    mname:'',
    lname:'',
    gender:'',
    country:'',
    city:'',
    language:[],
    game:[]
  });
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState('')

  const EditRow = (dat,i,r) => {
    console.log("iiiiiiiiii----->",i)
    setData({
      fname:i.fname,
      mname:i.mname,
      lname:i.lname,
      gender:i.gender,
      country:i.country,
      city:i.city,
      language:i.language,
      game:i.game
    })
    setIndex(r)
    console.log("rrrrrrr------->",r)
  }
  
  const DeleteRow = (dat,i,r) => {
    const Data = userData
    console.log("userData------->",userData)
    Data.splice(r,1)
    console.log("spliceData------>",Data)
    setUserData([...Data])
  }

const columns = [
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
  
      render:(dat,i,r) => (
        <>
          <EditOutlined onClick={() =>EditRow(dat,i,r)} />
          <DeleteOutlined onClick={() =>DeleteRow(dat,i,r)} />
        </>
      )
    }
  ];

  return(
    <Table dataSource={props.tableData} columns={columns} />
    // <Rtable userData={userData} DeleteRow={DeleteRow} EditRow={EditRow}></Rtable>
  )
 }

const mapStateToProps =(state) =>{
  return{
    tableData:state.data,
  }
}
 
export default connect(mapStateToProps,null) (Rtable);
