import React, { useState } from 'react'
import { Modal, Button } from 'antd';

const InputModal = () => {
    const [modalOpen, setModalOpen] = useState(false);

    const modalChange = (text) => {
        // setModalOpen({modalOpen, [e.target.name]:e.target.value})
        // console.log("e.target.name",e.target.name)
        // console.log("e.target.value",e.target.value)
        // console.log("modalOpen======>",modalOpen)
        setModalOpen(text)
      }
      
      const showModal = () => {
        setModalOpen(true);
      };
      
      const handleOk = () => {
        console.log("modalOpen----------->",modalOpen)
        setModalOpen({text:''})
        setModalOpen(false)
        
      };
      
      const handleCancel = () => {
        setModalOpen(false)
      };

  return (
    <>
    <div>
        
    </div>
      <Button type='primary' style={{margin:5,marginLeft:80}} onClick={showModal}>Open Modal</Button>
      <Modal title="Basic Modal" open={modalOpen} onOk={handleOk} onCancel={handleCancel}>
      <input autoComplete='off' type="text" placeholder='Text Here...' name='text' value={modalOpen.text} onChange={() => modalChange()} />
      </Modal>
    </>
  )
}
export default InputModal;
