import React, { useState, useEffect } from 'react';
import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import { userDelete, userEdit, userIndex } from '../Redux/Action'
import { Checkbox } from 'antd'

function Rtable (props) {
  
  const [data, setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]});
  const [userData, setUserData]=useState([])
  const [index, setIndex]=useState('')

  useEffect(() => setUserData(props.tableData),[props.tableData])
  console.log("useEffecttableData------>",props.tableData)

  axios
  .get("http://localhost:5000/users/create")
  .then((res) => {
    userData(res.data)
    console.log(res.data);
  })
  .catch((err) => {
    console.log("err");
  });

  const EditRow = (dat,i,r) => {
    console.log("iiiiiiiiii----->",i)
    console.log("edat------>",dat)
    setIndex(r)
    props.userEdit(i)
    props.userIndex(r)
    props.navigate("/")
    console.log("rrrrrrr------->",r)
  }
  
  const DeleteRow = (dat,i,r) => {
    console.log("ddat------->",dat)
    console.log("iiii------->",i)
    console.log("rrr------->",r)
    const Data = props.tableData;
    console.log("props.tableData------->",props.tableData)
    Data.splice(r,1)
    setUserData([...Data])
    console.log("spliceData------>",userData)
    props.userDelete(Data)
  }

  const GoBack = () =>{
    props.navigate("/")
  }

  const onSearch = (e) => {
    console.log("searchvalue------>",e.target.value)
    if(e.target.value === ""){
      setUserData(props.tableData)
    }
    else{
      const searchData = userData.filter(Item => Item.fname.includes(e.target.value) || Item.mname.includes(e.target.value) || Item.lname.includes(e.target.value))
      console.log("searchuserData",userData)
      setUserData(searchData)
      console.log("searchData",searchData)
    }
  }

const handleCheck = (e,check,r) => {
    const deleteRow = userData
    console.log("esfdgfgdf==========>", userData);
    console.log("deleteRow------>",deleteRow);
    if(check === "check"){
      if (e.target.checked === true){
        const index = userData.indexOf(e.target.name)
        deleteRow.splice(index, 1)
        console.log("e.target.name------>",e.target.name)
      }
      setData({
        ...data,userData:deleteRow
      })
    } else{
      setData({...data, [e.target.name]:e.target.value})
      }
}

const deleteAllRow = (e,check,r) => {
  console.log("eeeeeeeeee",e)
  console.log("sdghhjbsdkbsdkbsdk",check)
    const deleteRow = userData;
    console.log("userData------->",userData)
    setUserData([...deleteRow])
    console.log("splicedeleteData------>",deleteRow)
    props.userDelete(deleteRow)
 }

const column = [
    {
      title: '#',
      dataIndex: 'check',
      key: 'check',
      render:(e,check,r) => (
        <>
        <Checkbox
        type="checkbox"
        name=''
        value=''
        onChange={(e) => handleCheck(e,"check",r)} />
        </>
      )
    },
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
      render:(dat,i,r) => (
        <>
          <EditOutlined style={{color:'blue'}} onClick={() => EditRow(dat,i,r)} />
          <DeleteOutlined style={{color:'red', marginLeft:3}} onClick={() => DeleteRow(dat,i,r)} />
        </>
      )
    }
  ];

  return(
    <>
    <label style={{color:'blue'}}><u><b>Search TableData :</b></u></label>
    <input style={{ margin:10 , marginleft:20 }} type="text" placeholder='Search...' onChange={(e) => onSearch (e)} />
    <button onClick={(e,check,r) => deleteAllRow(e,check,r)}>Delete All</button><br />
    <Table dataSource={userData} columns={column} pagination={{ pageSize: 2 }} />
    <button onClick={GoBack}>Go Back</button>
    </>
  )
 };

const mapStateToProps = (state) =>{
  return{
    tableData:state.data,
    uEdit:state.UserEdit,
    uIndex:state.UserIndex,
  }
}
const mapDispatchToProps = (dispatch) =>{
  return{
   userDelete:(data) => dispatch(userDelete(data)),
   userEdit: (data) => dispatch(userEdit(data)),
   userIndex: (data) => dispatch(userIndex(data)),
  }
}
 
export default connect(mapStateToProps,mapDispatchToProps) (Rtable)
