import React, { useState } from 'react'
import { Table } from 'antd'
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';

const App = () => {

  const [data , setData]=useState({fname:'',mname:'',lname:''})
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState("")

const hendleOnChange = (e) => {
  console.log("eeeeeeeeeeeeeeeee",e.target.value)
  setData({...data, [e.target.name]:e.target.value})
}

const hendleOnSubmit = () => {
  const Data = userData
  console.log("Data---------->",Data)
  if(index === ""){
    Data.push(data)
  }
  else{
    Data[index] = data
    setIndex("")
  }
  setUserData([...Data])
  setData({fname:'',mname:'',lname:''})
}


const EditRow = (data,i,r) => {
  console.log("iiiiiiiiii----->",i)
  setData({fname:i.fname,mname:i.mname,lname:i.lname})
  setIndex(r)
  console.log("rrrrrrr------->",r)
}

const DeleteRow = (data,i,r) => {

}

const columns = [
  {
    title: 'FIRST NAME',
    dataIndex: 'fname',
    key: 'fname',
  },
  {
    title: 'MIDDLE NAME',
    dataIndex: 'mname',
    key: 'mname',
  },
  {
    title: 'LAST NAME',
    dataIndex: 'lname',
    key: 'lname',
  },
  {
    title: 'ACTION',
    dataIndex: 'action',
    key: 'action',

    render:(data,i,r) => (
      <>
        <EditOutlined onClick={() => EditRow(data,i,r)} />
        <DeleteOutlined onClick={() => DeleteRow(data,i,r)} />
      </>
    )
  },
];
  return (
    <>
    <div style={{ margin:10 , marginleft:20 }}>
      <label><b>FIRST NAME : </b></label>
      <input type="text" name='fname' placeholder='Enter Your First Name' value={data.fname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b>MIDDLE NAME : </b></label>
      <input type="text" name='mname' placeholder='Enter Your middle Name' value={data.mname} onChange={(e) => hendleOnChange(e)} /><br />
      <label><b>LAST NAME : </b></label>
      <input type="text" name='lname' placeholder='Enter Your last Name' value={data.lname} onChange={(e) => hendleOnChange(e)} /><br />
      <button style={{margin:5,marginLeft:90 }} onClick={() => hendleOnSubmit()} >Button</button>
      <Table dataSource={userData} columns={columns} />
    </div>
    </>
  )
}
export default App;
