import React, { useState, useEffect } from 'react';
import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import { userDelete, userEdit, userIndex } from '../Redux/Action'
import { checkbox } from 'antd'

function Rtable (props) {
  const [data, setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]});
  const [userData, setUserData]=useState([])
  const [index, setIndex]=useState('')

  useEffect(() => setUserData(props.tableData),[props.tableData])
  console.log("useEffecttableData------>",props.tableData)

  const EditRow = (dat,i,r) => {
    console.log("iiiiiiiiii----->",i)
    console.log("edat------>",dat)
    setIndex(r)
    props.userEdit(i)
    props.userIndex(r)
    props.navigate("/")
    console.log("rrrrrrr------->",r)
  }
  
  const DeleteRow = (dat,i,r) => {
    console.log("ddat------->",dat)
    const Data = props.tableData;
    console.log("props.tableData------->",props.tableData)
    Data.splice(r,1)
    setUserData([...Data])
    console.log("spliceData------>",userData)
    props.userDelete(Data)
  }

  const GoBack = () =>{
    props.navigate("/")
  }

  const onSearch = (e) => {
    console.log("searchvalue------>",e.target.value)
    if(e.target.value === ""){
      setUserData(props.tableData)
    }
    else{
      const searchData = userData.filter(Item => Item.fname.includes(e.target.value) || Item.mname.includes(e.target.value) || Item.lname.includes(e.target.value))
      console.log("searchuserData",userData)
      setUserData(searchData)
      console.log("searchData",searchData)
    }
  }

const column = [
    {
      title: 'CHECKED',
      dataIndex: 'check',
      key: 'check',
    },
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
  
      render:(dat,i,r) => (
        <>
        <Checkbox 
          type="checkbox"
          name='check'
          value='check'
          onChange={(e) => hendleChange(e)} 
          checked='' />
          <EditOutlined style={{color:'blue'}} onClick={() => EditRow(dat,i,r)} />
          <DeleteOutlined style={{color:'red' , marginLeft:3}} onClick={() => DeleteRow(dat,i,r)} />
        </>
      )
    }
  ];

  return(
    <>
    <label style={{color:'blue'}}><u><b>Search TableData : </b></u></label>
    <input style={{ margin:10 , marginleft:20 }} type="text" placeholder='Search...' onChange={(e) => onSearch (e)} /><br />
    <Table dataSource={userData} columns={column} />
    <button onClick={GoBack}>Go Back</button>
    </>
  )
 }

const mapStateToProps =(state) =>{
  return{
    tableData:state.data,
    uEdit:state.UserEdit,
    uIndex:state.UserIndex,
  }
}
const mapDispatchToProps=(dispatch) =>{
  return{
   userDelete:(data) => dispatch(userDelete(data)),
   userEdit: (data) => dispatch(userEdit(data)),
   userIndex: (data) => dispatch(userIndex(data))
  }
}
 
export default connect(mapStateToProps,mapDispatchToProps) (Rtable);
