import React, { useState, useEffect } from 'react';
import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import { userDelete, userEdit, userIndex } from '../Redux/Action'
import { Checkbox } from 'antd'

function Rtable (props) {
  
  const [data, setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]});
  const [userData, setUserData]=useState([])
  const [index, setIndex]=useState('')
  const [deleteall, setDeleteAll]=useState([])

  useEffect(() => setUserData(props.tableData),[props.tableData])
  console.log("useEffecttableData------>",props.tableData)
  // useEffect(() => setDeleteAll(props.tableData),[props.tableData])
  // console.log("useEffecttableDataaaaaaaaaaaaa------>",props.tableData)

  const EditRow = (dat,i,r) => {
    console.log("iiiiiiiiii----->",i)
    console.log("edat------>",dat)
    setIndex(r)
    props.userEdit(i)
    props.userIndex(r)
    props.navigate("/")
    console.log("rrrrrrr------->",r)
  }
  
  const DeleteRow = (dat,i,r) => {
    console.log("ddat------->",dat)
    const Data = props.tableData;
    console.log("props.tableData------->",props.tableData)
    Data.splice(r,1)
    setUserData([...Data])
    console.log("spliceData------>",userData)
    props.userDelete(Data)
  }

  const GoBack = () =>{
    props.navigate("/")
  }

  const onSearch = (e) => {
    console.log("searchvalue------>",e.target.value)
    if(e.target.value === ""){
      setUserData(props.tableData)
    }
    else{
      const searchData = userData.filter(Item => Item.fname.includes(e.target.value) || Item.mname.includes(e.target.value) || Item.lname.includes(e.target.value))
      console.log("searchuserData",userData)
      setUserData(searchData)
      console.log("searchData",searchData)
    }
  }

  const hendleCheck = (e,check,r) => {
    console.log("eeeeeee",e)
    console.log("checkkkkk",check)
    console.log("rrrrrrrrrrrrr",r)
    console.log("hiiiiiii")
    // const tableData = data
    // if (check === "check"){
    //   if (e.target.checked=== true){
    //     console.log("hiii")
    //   tableData.push(e.target.name)
    //   }
    //   else{
    //     const index = data.check.indexOf(e.target.name)
    //     check.splice(index, 1)
    //   }
    //   setData({
    //     ...data,check:tableData
    //   })
    // }
    const deleteData = userData
    deleteData.splice(r,1)
    console.log("deleteData------>",deleteData)
}
  const deleteAllRow = (e,check,r) =>{
    const Data = props.tableData
    data.splice(r,1)
    setUserData([...Data])
    console.log("spliceData------>",userData)
    props.userDelete(Data)

}

const column = [
    {
      title: 'CHECKED',
      dataIndex: 'check',
      key: 'check',
      render:(e,check,r) => (
        <>
        <Checkbox onChange={(e) => hendleCheck(e,check,r)} />
        </>
      )
    },
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
      render:(dat,i,r) => (
        <>
          <EditOutlined style={{color:'blue'}} onClick={() => EditRow(dat,i,r)} />
          <DeleteOutlined style={{color:'red', marginLeft:3}} onClick={() => DeleteRow(dat,i,r)} />
        </>
      )
    }
  ];

  return(
    <>
    <label style={{color:'blue'}}><u><b>Search TableData :</b></u></label>
    <input style={{ margin:10 , marginleft:20 }} type="text" placeholder='Search...' onChange={(e) => onSearch (e)} />
    <button onClick={(e,r) => deleteAllRow(e,r)}>Delete All</button><br />
    <Table dataSource={userData} columns={column} />
    <button onClick={GoBack}>Go Back</button>
    </>
  )
 };

const mapStateToProps = (state) =>{
  return{
    tableData:state.data,
    uEdit:state.UserEdit,
    uIndex:state.UserIndex,
  }
}
const mapDispatchToProps = (dispatch) =>{
  return{
   userDelete:(data) => dispatch(userDelete(data)),
   userEdit: (data) => dispatch(userEdit(data)),
   userIndex: (data) => dispatch(userIndex(data)),
  }
}
 
export default connect(mapStateToProps,mapDispatchToProps) (Rtable);
