import React from 'react';
import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import { useState } from 'react';
import { userDelete, userEdit, userIndex } from '../Redux/Action' 

const Rtable = (props) =>{
  const [data, setData]=useState({fname:'',mname:'',lname:'',gender:'',country:'',city:'',language:[],game:[]});
  const [userData, setUserData]=useState([])
  const [index, setIndex]=useState('')

  const EditRow = (dat,i,r) => {
    console.log("iiiiiiiiii----->",i)
    setIndex(r)
    props.userEdit(i)
    props.userIndex(r)
    props.navigate('/')
    console.log("rrrrrrr------->",r)
  }
  
  const DeleteRow = (dat,i,r) => {
    let Data = props.tableData;
    console.log("props.tableData------->",props.tableData)
    Data.splice(r,1)
    console.log("spliceData------>",Data)
    setUserData(Data)
    props.userDelete(Data)
  }

  // const GoBack = () =>{
  //   props.navigate('/')
  // }

const columns = [
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
  
      render:(record) => (
        <>
          <EditOutlined style={{color:'blue'}} onClick={() => EditRow(record)} />
          <DeleteOutlined style={{color:'red' , marginLeft:3}} onClick={() => DeleteRow(record)} />
        </>
      )
    }
  ];

  return(
    <>
    <Table dataSource={props.tableData} columns={columns} />
    {/* <Table userData={props.tableData} DeleteRow={DeleteRow} EditRow={EditRow}></Table>
    <button onClick={GoBack}>Go Back</button> */}
    </>
  )
 }

const mapStateToProps =(state) =>{
  return{
    tableData:state.data,
    uEdit:state.UserEdit,
    uIndex:state.UserIndex,
  }
}
const mapDispatchToProps=(dispatch) =>{
  return{
   userDelete:(data) => dispatch(userDelete(data)),
   userEdit: (data) => dispatch(userEdit(data)),
   userIndex: (data) => dispatch(userIndex(data))
  }
}
 
export default connect(mapStateToProps,mapDispatchToProps) (Rtable);
