import React from 'react';
import { Table } from 'antd';
import { EditOutlined , DeleteOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import { useState } from 'react';
import {userSubmit,userDelete,userEdit,userIndex} from '../Redux/Action' 

const Rtable = (props) =>{
  const [data , setData]=useState({
    fname:'',
    mname:'',
    lname:'',
    gender:'',
    country:'',
    city:'',
    language:[],
    game:[]
  });
  const [userData , setUserData]=useState([])
  const [index , setIndex]=useState('')

  // const EditRow = (dat,i,r) => {
  //   console.log("iiiiiiiiii----->",i)
  //   // setData({
  //   //   fname:i.fname,
  //   //   mname:i.mname,
  //   //   lname:i.lname,
  //   //   gender:i.gender,
  //   //   country:i.country,
  //   //   city:i.city,
  //   //   language:i.language,
  //   //   game:i.game
  //   // })
  //   setIndex(r)
  //   props.UserEdit(i)
  //   props.userIndex(r)
  //   props.navigate('/')
  //   console.log("rrrrrrr------->",r)
  // }
  
  const DeleteRow = (dat,i,r) => {
    let Data = userData
    // console.log("userData------->",tableData)
    Data.splice(r,1)
    console.log("spliceData------>",Data)
    setUserData([...Data])
    props.userdelete(Data)
  }

const columns = [
    {
      title: 'FIRST NAME',
      dataIndex: 'fname',
      key: 'fname',
    },
    {
      title: 'MIDDLE NAME',
      dataIndex: 'mname',
      key: 'mname',
    },
    {
      title: 'LAST NAME',
      dataIndex: 'lname',
      key: 'lname',
    },
    {
      title: 'GENDER',
      dataIndex: 'gender',
      key: 'gender',
    },
    {
      title: 'COUNTRY',
      dataIndex: 'country',
      key: 'country',
    },
    {
      title: 'CITY',
      dataIndex: 'city',
      key: 'city',
    },
    {
      title: 'LANGUAGE',
      dataIndex: 'language',
      key: 'language',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'GAME',
      dataIndex: 'game',
      key: 'game',
      render:(r)=>(
        <>
        {r + ''}
        </>
      )
    },
    {
      title: 'ACTION',
      dataIndex: 'action',
      key: 'action',
  
      render:(dat,i,r) => (
        <>
          <EditOutlined onClick={() => props.EditRow(dat,i,r)} />
          <DeleteOutlined onClick={() => DeleteRow(dat,i,r)} />
        </>
      )
    }
  ];

  return(
    <Table dataSource={props.tableData} columns={columns} />
    // <Rtable userData={userData} DeleteRow={DeleteRow} EditRow={EditRow}></Rtable>
  )
 }

const mapStateToProps =(state) =>{
  return{
    tableData:state.data,
  }
}
const mapDispatchToProps=(dispatch) =>{
  return{
   userSubmit:(data) => dispatch(userSubmit(data)),
   userDelete:(data) => dispatch(userDelete(data)),
  //  UserEdit: (data) => dispatch(userEdit(data)),
  //  UserIndex: (data) => dispatch(userIndex(data))
  }
}
 
export default connect(mapStateToProps,mapDispatchToProps) (Rtable);
